onClick:
{{
  (() => {
    const row = ListeAffaires.triggeredRow;
    if (!row) {
      showAlert("Aucune affaire sélectionnée", "warning");
      return;
    }

    CloturerAffaire.run()
      .then(() => {
        // Lance le workflow n8n
        return DeplacerDossierAffaire.run();
      })
      .then(() => {
        showAlert("Affaire clôturée et dossier déplacé ✅", "success");
        return GetAffaires.run();
      })
      .then(() => {
			GetAffaires.data
        // Fermeture de la popup
			closeModal(ModalConfirmationCloture.name);   // <-- mets ici le nom réel de ta popup
      })
      .catch((e) => {
        console.log("Erreur clôture / déplacement", e);
        showAlert("Erreur lors de la clôture ou du déplacement du dossier", "error");
      });
  })()
}}
