onClick:
{{
  (async () => {
    try {
      // 1) Création / vérification du dossier sur le réseau via n8n
      const res = await CreerDossierAffaire.run();

      // On essaie de récupérer le 1er élément utile dans la réponse
      const payload =
        res && typeof res === "object"
          ? (
              Array.isArray(res.body)
                ? res.body[0]
                : (res.body && typeof res.body === "object"
                    ? res.body
                    : (Array.isArray(res) ? res[0] : res)
                  )
            )
          : {};

      const stdout = (payload.stdout || "").trim();

      if (stdout === "ALREADY_EXISTS") {
        showAlert("Le dossier de l'affaire existe déjà sur le réseau", "warning");
        // On arrête le flux ici : on ne crée pas l'affaire en base
        return;
      }

      if (stdout === "CREATED") {
        showAlert("Dossier de l'affaire créé sur le réseau", "success");
      } else {
        // Cas où n8n renvoie autre chose que prévu
        showAlert("Réponse inattendue de n8n lors de la création du dossier.", "error");
        return;
      }

      // 2) Ajout de l'affaire dans la base
      try {
        await AjoutAffaire.run();
        showAlert("Affaire créée en base", "success");
      } catch (e) {
        showAlert("Erreur lors de la création de l'affaire en base", "error");
        throw e; // remonte l'erreur au catch global
      }

      // 3) Rafraîchir le tableau
      await GetAffaires.run();

      // 4) Fermer la modal
      closeModal(ModalCreerAffaire.name);

    } catch (e) {
      showAlert("Erreur : " + (e.message || e), "error");
    }
  })()
}}
