onClick:
{{
  (async () => {
    const isPrel = SwitchPREL.isSwitchedOn;

    const numero_affaire  = ListeAffaires.selectedRow.numero_affaire;
    const repere = InputRepereAppareil.text;
    const numero_appareil = numero_affaire + repere;

    const delai = DateDelai.selectedDate;

    // Champs utiles seulement pour le parcours "classique"
    const heuresPrevues = Number(InputHeuresPrevues.text || 0) || null;
    const nombreJoursMarge = InputNbJoursMarge.text;
    const heuresPrevuesMontage = heuresPrevues ? 0.7 * heuresPrevues : null;

    // 1) Créer l'arborescence (selon le type)
    if (isPrel) {
      await CreerArborescencePrelancement.run({ numero_affaire, numero_appareil })
        .then(() => showAlert("Arborescence pré-lancement créée sur le réseau", "success"))
        .catch((e) => {
          showAlert("Erreur lors de la création de l'arborescence pré-lancement", "error");
          throw e;
        });
    } else {
      await CreerArborescence.run()
        .then(() => showAlert("Arborescence créée sur le réseau", "success"))
        .catch((e) => {
          showAlert("Erreur lors de la création de l'arborescence", "error");
          throw e;
        });
    }

    // 2) Insérer l'appareil (requête différente en PREL)
    if (isPrel) {
      await AjoutAppareilPrelancement.run({
        numero_affaire,
        numero_appareil,
        nom_appareil: InputNomAppareil.text,
				repere_appareil: repere,
      })
        .then(() => showAlert("Pré-lancement créé dans Kynthos Flow", "success"))
        .catch((e) => {
          showAlert("Erreur lors de la création du pré-lancement dans Kynthos Flow", "error");
          throw e;
        });
    } else {
      await AjoutAppareil.run({
        numero_affaire,
        numero_appareil,
        nom_appareil: InputNomAppareil.text,
        repere_appareil: repere,
        delai,
      })
        .then(() => showAlert("Appareil créé dans Kynthos Flow", "success"))
        .catch((e) => {
          showAlert("Erreur lors de la création de l'appareil dans Kynthos Flow", "error");
          throw e;
        });
    }

    // 3) Initialiser le suivi de fabrication (UNIQUEMENT si classique)
    if (!isPrel) {
      await InitWorkflowSuiviAppareil.run({
        numero_appareil,
        nombre_jours_marge: nombreJoursMarge,
        heures_prevues: heuresPrevues,
        heures_prevues_montage: heuresPrevuesMontage,
      })
        .then(() => showAlert("Suivi de fabrication initialisé", "success"))
        .catch((e) => {
          showAlert("Erreur lors de l'initialisation du suivi de fabrication", "error");
          throw e;
        });
    }

    // 4) Rafraîchir la table
    await GetAppareils.run({ numero_affaire });

    // 5) Fermer la modal
    closeModal(ModalCreerAppareil.name);

  })().catch((e) => {
    showAlert("Erreur : " + (e.message || e), "error");
  })
}}
