onClick:
{{
  (async () => {
    const numero_affaire  = ListeAffaires.selectedRow.numero_affaire;
    const numero_appareil = ListeAffaires.selectedRow.numero_affaire + InputRepereAppareil.text;

    const heuresPrevues = Number(InputHeuresPrevues.text || 0) || null;
		const nombreJoursMarge = InputNbJoursMarge.text;
    const delai         = DateDelai.selectedDate;
		const heuresPrevuesMontage = 0.7*heuresPrevues;

    // 1) Créer l'arborescence
    await CreerArborescence.run()
      .then(() => showAlert("Arborescence créée sur le réseau", "success"))
      .catch((e) => { 
        showAlert("Erreur lors de la création de l'arborescence", "error"); 
        throw e; 
      });

    // 2) Insérer l'appareil (dont le délai)
    await AjoutAppareil.run({
      numero_affaire,
      numero_appareil,
      nom_appareil: InputNomAppareil.text,
      repere_appareil: InputRepereAppareil.text,
      delai,  // si ta requête AjoutAppareil le prend en param
    })
      .then(() => showAlert("Appareil créé dans Databook", "success"))
      .catch((e) => { 
        showAlert("Erreur lors de la création de l'appareil dans Databook", "error"); 
        throw e; 
      });

    // 3) Initialiser le suivi de fabrication (workflow_*)
    await InitWorkflowSuiviAppareil.run({
      numero_appareil,
			nombre_jours_marge: nombreJoursMarge,
      heures_prevues: heuresPrevues,
			heures_prevues_montage: heuresPrevuesMontage,
    })
      .then(() => showAlert("Suivi de fabrication initialisé", "success"))
      .catch((e) => {
        showAlert("Erreur lors de l'initialisation du suivi de fabrication", "error");
        throw e;
      });

    // 4) Rafraîchir la table
    await GetAppareils.run({ numero_affaire });

    // 5) Fermer la modal
    closeModal(ModalCreerAppareil.name);

    // 6) Rediriger vers la fiche appareil
    //await navigateTo("Fiche appareil", { numero_appareil }, "SAME_WINDOW");

  })().catch((e) => {
    showAlert("Erreur : " + (e.message || e), "error");
  })
}}
