onClick:
{{
  // 1) Supprimer le dossier sur le NAS (remplace API_SupprimerDossier par ton action API n8n)
  SupprimerAppareil.run({
    // passe ce dont ton n8n a besoin ; au minimum l'identifiant
    numero_appareil: ListeAppareils.selectedRow.numero_appareil
    // ex. numero_affaire, chemin_complet, etc. si ton workflow les attend
  })
  // 2) Puis supprimer en base
  .then(() => DeleteAppareil.run())
  // 3) Feedback + refresh
  .then(() => {
		if (ListeAffaires.selectedRow.numero_affaire) {
      GetAppareils.run();
    } else {
      GetAllAppareils.run();
    }
    showAlert('Appareil supprimé', 'success');
		closeModal(ModalSuppression.name)
    GetAppareils.run();
  })
  .catch((e) => {
    showAlert('Échec de la suppression : ' + (e?.message || ''), 'error');
  });
}}
