validation.isColumnEditableCellRequired:
{{
  (
    (isNewRow, currentIndex, currentRow) => (

  (TableAlertRules.isAddRowInProgress
    ? TableAlertRules.newRow.event_type
    : currentRow.event_type
  ) !== "STATUS_CHANGED"

    ))
    (
      TableAlertRules.isAddRowInProgress,
      TableAlertRules.isAddRowInProgress ? -1 : TableAlertRules.editableCell.index,
      TableAlertRules.isAddRowInProgress ? TableAlertRules.newRow : (TableAlertRules.processedTableData[TableAlertRules.editableCell.__originalIndex__] ||
        Object.keys(TableAlertRules.processedTableData[0])
          .filter(key => ["__originalIndex__", "__primaryKey__"].indexOf(key) === -1)
          .reduce((prev, curr) => {
            prev[curr] = "";
            return prev;
          }, {}))
    )
  }}

