onClick:
{{
  (async () => {
    // 1) Création du dossier sur le réseau
    await CreerDossierAffaire.run()
      .then(() => showAlert("Dossier de l'affaire créé sur le réseau", "success"))
      .catch((e) => {
        showAlert("Échec de la création du dossier de l'affaire sur le réseau", "error");
        throw e; // stoppe la suite si erreur
      });

    // 2) Ajout de l'affaire dans la base
    await AjoutAffaire.run()
      .then(() => showAlert("Affaire créée en base", "success"))
      .catch((e) => {
        showAlert("Erreur lors de la création de l'affaire en base", "error");
        throw e;
      });

    // 3) Rafraîchir le tableau
    await GetAffaires.run();

    // 4) Fermer la modal
    closeModal(ModalCreerAffaire.name);
  })().catch((e) => {
    showAlert("Erreur : " + (e.message || e), "error");
  })
}}
