onClick:
{{
  (async () => {
    const numero_affaire = appsmith.URL.queryParams.numero_affaire;
		const numero_appareil = appsmith.URL.queryParams.numero_affaire+InputRepereAppareil.text;

    // 1) Créer l'arborescence
    await CreerArborescence.run()
      .then(() => showAlert("Arborescence créée sur le réseau", "success"))
      .catch((e) => { 
        showAlert("Erreur lors de la création de l'arborescence", "error"); 
        throw e; 
      });

    // 2) Insérer l'appareil
    await AjoutAppareil.run()
      .then(() => showAlert("Appareil créé dans Databook", "success"))
      .catch((e) => { 
        showAlert("Erreur lors de la création de l'appareil dans Databook", "error"); 
        throw e; 
      });

    // 3) Rafraîchir la table
    await GetAppareils.run({ numero_affaire });

    // 4) Fermer la modal
		  closeModal(ModalCreerAppareil.name);

		// 5) Rediriger vers la fiche appareil avec le paramètre
    await navigateTo("Fiche appareil", { numero_appareil }, "SAME_WINDOW");

  })().catch((e) => {
    showAlert("Erreur : " + (e.message || e), "error");
  })
}}
