onClick:
{{
  (async () => {
    const lang   = (appsmith.store.langue_appareil || (RadioLangue.selectedOptionValue || 'fr')).toLowerCase();
    const rows   = SommaireUI.annexList();              // chapitre 8 + sous-chapitres actifs (déjà flatten)
    const drafts = appsmith.store.annex_drafts || {};

    // calcule les lignes modifiées
    const changes = rows.map(r => {
      const initial = (lang === 'en'
        ? (r.label_en || r.label_fr || '')
        : (r.label_fr || r.label_en || '')
      );
      const val = (drafts[r.master_id] ?? initial);
      return (val !== initial) ? { master_id: r.master_id, new_label: val } : null;
    }).filter(Boolean);

    if (!changes.length) {
      closeModal('ModalAnnexes');
      showAlert('Aucune modification', 'info');
      return;
    }

    // updates en parallèle
    await Promise.all(
      changes.map(it =>
        RenameSommaireItem.run({
          master_id: it.master_id,
          new_label: it.new_label,
          lang
        })
      )
    );

    await GetSommaireAppareil.run();        // refresh liste principale
    await storeValue('annex_drafts', null); // nettoie le brouillon
    closeModal('ModalAnnexes');
    showAlert('Libellés mis à jour', 'success');
  })().catch(e => showAlert('Erreur sauvegarde: ' + (e?.message || e), 'error'))
}}
