onClick:
{{
  (async () => {
    const lang   = (appsmith.store.langue_appareil || (RadioLangue.selectedOptionValue || 'fr')).toLowerCase();
    const rows   = SommaireUI.annexList();      // chapitre 8 + ses sous-chapitres actifs (flatten)
    const drafts = appsmith.store.annex_drafts || {};

    // util pour récupérer le numéro N (8.1 -> 1, 8.2 -> 2, etc.)
    const subNo = (r) => Number(String(r.effDisplayNo || r.display_no || "").split(".").pop());

    // construit la liste des changements *sans* rafraîchir l'UI
    const changes = rows.map(r => {
      const prev = SommaireUI.labelFor(r);                // libellé affiché actuellement (avant sauvegarde)
      const next = drafts[r.master_id] ?? prev;           // libellé saisi dans la modal (si modifié)
      return (next !== prev)
        ? { master_id: r.master_id, no: subNo(r), prev_label: prev, new_label: next }
        : null;
    }).filter(Boolean);

    if (!changes.length) {
      closeModal('ModalAnnexes');
      showAlert('Aucune modification', 'info');
      return;
    }

    // 1) MET À JOUR LA DB (labels override) pour chaque item modifié
    await Promise.all(
      changes.map(c =>
        RenameSommaireItem.run({
          master_id: c.master_id,
          new_label: c.new_label,
          lang
        })
      )
    );

    // 2) APPEL N8N : ne lui envoie que {no, label, prev_label}
    const annexesPayload = changes.map(c => ({
      no: c.no,
      label: c.new_label,
      prev_label: c.prev_label
    }));

    await RenommerDossiersAnnexes.run({ annexes: annexesPayload });

    // 3) REFRESH + nettoyage
    await GetSommaireAppareil.run();
    await storeValue('annex_drafts', null);
    closeModal('ModalAnnexes');
    showAlert('Libellés mis à jour et dossiers renommés', 'success');
  })().catch(e => showAlert('Erreur sauvegarde: ' + (e?.message || e), 'error'))
}}
