onClick:
{{
  (async () => {
    const cand = ListeCandidatsCCPU.selectedRow;

    if (!cand) {
      showAlert("Sélectionne d'abord un fichier candidat.", "warning");
      return;
    }

    const rows = appsmith.store.ccpuSelectedRows || [];
    if (!rows.length) {
      showAlert("Aucune ligne à alimenter (sélection perdue, rouvre la fenêtre).", "error");
      return;
    }

    const numAff = CaracteristiquesAppareils.data?.[0]?.numero_affaire;
    const nomCli = DonneesGeneralesAffaire.data?.[0]?.nom_client;
    const nomApp = CaracteristiquesAppareils.data?.[0]?.nom_appareil;

    const tasks = rows.map(r =>
      CopyCCPUFromCandidate.run({
        srcPath: cand.path,
        rep: r.rep,
        srsm: (r.srsm == null || String(r.srsm).trim() === "") ? "1" : String(r.srsm).trim(),
        code: r.code,
        numero_affaire: numAff,
        nom_client: nomCli,
        nom_appareil: nomApp,
      })
    );

    const results = await Promise.allSettled(tasks);
    const ok = results.filter(r => r.status === "fulfilled").length;
    const ko = results.length - ok;

    showAlert(
      `Copie terminée — OK: ${ok}${ko ? " / Échecs: " + ko : ""}`,
      ko ? "warning" : "success"
    );

    // Rafraîchir les statuts en base
    await QryCCPUStatus.run();
		// Rafraîchir la liste
    await QryCCPUManquants.run();

    closeModal(ModalChoixCCPU.name);
  })()
}}
