isVisible:
{{
(() => {
  const cand = ListeCandidatsCCPU.selectedRow;
  const url  = appsmith.store.ccpuCandidateUrl;

  // Pas de candidat, pas de path, pas d'URL → invisible
  if (!cand || !cand.path || !url) return false;

  // Code attendu = code de la première ligne pré-sélectionnée
  const rows = appsmith.store.ccpuSelectedRows || [];
  if (!rows.length) return false;

  const expectedCode = String(rows[0].code || "").trim();
  if (!expectedCode) return false;

  // On récupère le paramètre ?code=... dans l'URL
  const match = url.match(/[?&]code=([^&]+)/);
  if (!match) return false;

  const urlCode = decodeURIComponent(match[1] || "");

  // Si différent → invisible
  if (urlCode !== expectedCode) return false;

  // Tout est cohérent → visible
  return true;
})()
}}
