onClick:
{{
  // 1) Génère/stocke le jobId pour ce run
  storeValue('jobId', ProgressCtl.newJobId())
    // 2) démarre le polling de progression
    .then(() => { ProgressCtl.startPolling(); })
    // 3) lance le workflow n8n
    .then(() => SendToN8n.run())
    // 4) une fois n8n terminé, recharge les infos depuis Neon
    .then(() => Promise.all([
      QryCCPUAttendus.run(),
      QryCCPUStatus.run(),
    ]))
    // 5) calcule le nombre de non traités et affiche l'alerte
    .then(() => {
      const all     = QryCCPUAttendus.data || [];
      const treated = QryCCPUStatus.data || [];

      const treatedSet = new Set(
        treated.map(r => `${r.rep}|${r.srsm}|${r.code}`)
      );

      const missingCount = all.filter(
        r => !treatedSet.has(`${r.rep}|${r.srsm}|${r.code}`)
      ).length;

      if (missingCount > 0) {
        showAlert(`${missingCount} CCPU manquant${missingCount > 1 ? 's' : ''}`, 'warning');
      } else {
        showAlert('Dossier généré — aucun CCPU manquant', 'success');
      }
    })
    // 6) gestion d'erreur
    .catch((e) => {
      showAlert('Erreur : ' + (e?.message || "échec de l'envoi"), 'error');
    })
    // 7) arrêt du polling + dernier refresh
    .finally(() => {
      ProgressCtl.stopPolling();
      GetProgress.run();
    });
}}
