onClick:
{{
  // 0) reset affichage
  storeValue('missingList', [])
    .then(() => storeValue('notFoundCount', 0))

    // 1) génère/stocke le jobId pour ce run
    .then(() => storeValue('jobId', ProgressCtl.newJobId()))

    // 2) démarre le polling de progression
    .then(() => { ProgressCtl.startPolling(); })

    // 3) lance le workflow n8n
    .then(() => SendToN8n.run())

    // 4) traite la réponse n8n
    .then((res) => {
      const payload = Array.isArray(res) ? res[0] : res;

      const list  = Array.isArray(payload?.missingList) ? payload.missingList : [];
      const count = Number(payload?.notFoundCount ?? list.length);

      // stocke pour la table (on conserve tous les éléments)
      return Promise.all([
        storeValue('missingList', list),
        storeValue('notFoundCount', count),
      ]).then(() => payload);
    })

    // 5) feedback utilisateur
    .then((payload) => {
      const count = Number(payload?.notFoundCount ?? 0);
      if (count > 0) {
        showAlert(`${count} CCPU manquant${count > 1 ? 's' : ''}`, 'warning');
      } else {
        showAlert('Dossier généré — aucun CCPU manquant', 'success');
      }
    })

    // 6) gestion d'erreur
    .catch((e) => {
      showAlert('Erreur : ' + (e?.message || "échec de l'envoi"), 'error');
    })

    // 7) arrêt du polling + dernier refresh
    .finally(() => {
      ProgressCtl.stopPolling();
      GetProgress.run();
    });
}}
