isDisabled:
{{
(() => {
  const rows = ListeCCPU.selectedRows || [];

  // Désactivé si aucune ligne
  if (!rows.length) return true;

  // Récupère les codes sélectionnés
  const codes = Array.from(
    new Set(rows.map(r => String(r.code || "").trim()))
  );

  // Désactivé si plusieurs codes différents
  return codes.length !== 1;
})()
}}

onClick:
{{
(async () => {
  const rows = ListeCCPU.selectedRows || [];

  if (!rows.length) {
    showAlert("Veuillez sélectionner au moins une ligne", "warning");
    return;
  }

  // Vérifier que tous les codes sont identiques
  const codes = Array.from(new Set(rows.map(r => String(r.code || "").trim())));
  if (codes.length !== 1) {
    showAlert("Toutes les lignes sélectionnées doivent avoir le même CCPU.", "error");
    return;
  }
  const code = codes[0];

  // Vérifier que tous les search_status sont identiques
  const statuses = Array.from(new Set(rows.map(r => r.search_status)));
  if (statuses.length !== 1) {
    showAlert("Toutes les lignes sélectionnées doivent avoir le même état de recherche.", "error");
    return;
  }
  const status = statuses[0];

  // nom_appareil au format "260999A - CUVE"
  const nomAppareil = CaracteristiquesAppareils.data?.[0]?.nom_appareil;
  if (!nomAppareil) {
    showAlert("Nom d'appareil introuvable", "error");
    return;
  }

  // Cas 1 : found_unique -> on affiche directement le CCPU
  if (status === "found_unique") {
    const url =
      `https://n8n.kynthos.fr/webhook/appsmith-ccpu-file` +
      `?nom_appareil=${encodeURIComponent(nomAppareil)}` +
      `&code=${encodeURIComponent(code)}`;

    storeValue("ccpuUrl", url);
    return;
  }

  // Cas 2 : need_manual_validation -> on ouvre la modale de choix
  if (status === "need_manual_validation") {
    const res = await ListCCPUCandidats.run({ code });
    const payload = Array.isArray(res) ? res[0] : res;
    const candidates = payload?.candidates || [];

    if (!candidates.length) {
      showAlert(`Aucun fichier candidat trouvé pour le code ${code}.`, "warning");
      return;
    }

    await storeValue("ccpuCandidates", candidates);
    await storeValue("ccpuSelectedRows", rows);

    showModal(ModalChoixCCPU.name);
    return;
  }

  // Autres cas : rien à faire
  showAlert("Les lignes sélectionnées ne sont ni 'Trouvé' ni 'À vérifier'.", "warning");
})()
}}

text:
{{
(() => {
  const rows = ListeCCPU.selectedRows || [];
  if (!rows.length) return "Voir le CCPU";

  // tous les codes identiques ?
  const codes = Array.from(new Set(rows.map(r => String(r.code || "").trim())));
  if (codes.length !== 1) return "Voir le CCPU";

  // états uniques
  const statuses = Array.from(new Set(rows.map(r => r.search_status)));

  if (statuses.length !== 1) return "Voir le CCPU";

  const status = statuses[0];

  if (status === "found_unique") return "Voir le CCPU";
  if (status === "need_manual_validation") return "Choisir le CCPU";

  return "Voir le CCPU";
})()
}}
