onClick:
{{
  (async () => {
    const rows = SommaireUI.annexList();
    const lang = (appsmith.store.langue_appareil || (RadioLangue.selectedOptionValue || 'fr')).toLowerCase();
    const init = {};
    rows.forEach(r => {
      init[r.master_id] = (lang === 'en'
        ? (r.label_en || r.label_fr || '')
        : (r.label_fr || r.label_en || '')
      );
    });
    await storeValue('annex_drafts', init);
    showModal('ModalAnnexes');
  })().catch(e => showAlert('Erreur ouverture modal: ' + (e?.message || e), 'error'))
}}
