onClick:
{{
  (async () => {
    const rows = ListeCCPUManquants.selectedRows || [];

    if (!rows.length) {
      showAlert("Sélectionne au moins une ligne.", "warning");
      return;
    }

    // On vérifie que toutes les lignes ont le même code
    const codes = Array.from(
      new Set(rows.map(r => String(r.code || "").trim()))
    );
    if (codes.length !== 1) {
      showAlert("Toutes les lignes sélectionnées doivent avoir le même CCPU.", "error");
      return;
    }

    const code = codes[0];

    // appel n8n pour lister les candidats
    const res = await ListCCPUCandidats.run({ code });
    const payload = Array.isArray(res) ? res[0] : res;
    const candidates = payload?.candidates || [];

    if (!candidates.length) {
      showAlert(`Aucun fichier candidat trouvé pour le code ${code}.`, "warning");
      return;
    }

    // on stocke pour la modal de choix
    await storeValue("ccpuCandidates", candidates);
    await storeValue("ccpuSelectedRows", rows);

    showModal(ModalChoixCCPU.name);
  })()
}}
