onClick:
{{
(() => {
  const rows = ListeCCPU.selectedRows || [];

  if (!rows.length) {
    showAlert("Veuillez sélectionner au moins une ligne", "warning");
    return;
  }

  // Vérifier que tous les codes sont identiques
  const firstCode = rows[0].code;
  const hasMismatch = rows.some(r => r.code !== firstCode);

  if (hasMismatch) {
    showAlert("Les lignes sélectionnées doivent avoir le même code CCPU", "error");
    return;
  }

  const code = firstCode;

  // nom_appareil au format "260999A - CUVE"
  const nomAppareil = CaracteristiquesAppareils.data?.[0]?.nom_appareil;
  if (!nomAppareil) {
    showAlert("Nom d'appareil introuvable", "error");
    return;
  }

  // URL du webhook n8n qui renvoie directement le PDF
  const url =
    `https://n8n.kynthos.fr/webhook/appsmith-ccpu-file` +
    `?nom_appareil=${encodeURIComponent(nomAppareil)}` +
    `&code=${encodeURIComponent(code)}`;

  // On stocke l'URL pour que le Document Viewer l'utilise
  storeValue("ccpuUrl", url);

})()
}}
