onClick:
{{
  (async () => {
    try {
      const selected = ListeCCPU.selectedRows || [];

      // --- CAS 1 : des lignes sont sélectionnées -> relance uniquement celles-ci ---
      if (selected.length) {
        // Appel du workflow n8n spécial "recheck sélection"
        // (la requête HTTP RecheckCCPUSelection doit déjà être configurée côté Appsmith
        //  avec numero_affaire, nom_appareil + rows = selectedRows)
        await RecheckCCPUSelection.run();

        // On recharge la liste complète
        await QryCCPUFull.run();

        showAlert(
          `Vérification relancée pour ${selected.length} ligne(s) sélectionnée(s)`,
          "success"
        );
        return;
      }

      // --- CAS 2 : aucune ligne sélectionnée -> première vérification complète ---
      const res = await CheckCCPU.run();
      const payload = Array.isArray(res) ? res[0] : res;

      await QryCCPUFull.run();

      const count = Number(payload?.notFoundCount ?? 0);
      if (count > 0) {
        showAlert(
          `${count} CCPU nécessitent une vérification manuelle`,
          "warning"
        );
      } else {
        showAlert(
          "Tous les CCPU ont été trouvés automatiquement",
          "success"
        );
      }
    } catch (e) {
      showAlert(
        "Erreur lors de la vérification des CCPU : " + (e?.message || ""),
        "error"
      );
    }
  })()
}}

text:
{{
  (() => {
    const sel = ListeCCPU.selectedRows || [];
    return sel.length
      ? "Relancer la vérification de la sélection"
      : "Lancer la vérification des CCPU";
  })()
}}
