onClick:
{{
  (async () => {
    try {
      // 1) appel du workflow n8n (écrit les manquants dans ccpu_copies_manuelles)
      const res = await CheckCCPU.run();
      const payload = Array.isArray(res) ? res[0] : res;

      // 2) recharge la liste des CCPU manquants (vue Statut = Traité / Non traité)
      await QryCCPUManquants.run();

      // 3) feedback utilisateur
      const count = Number(payload?.notFoundCount ?? 0);
      if (count > 0) {
        showAlert(
          `${count} CCPU nécessitent une vérification manuelle`,
          "warning"
        );
      } else {
        showAlert(
          "Tous les CCPU ont été trouvés et copiés automatiquement",
          "success"
        );
      }
    } catch (e) {
      showAlert(
        "Erreur lors de la vérification des CCPU : " + (e?.message || ""),
        "error"
      );
    }
  })()
}}
