isVisible:
{{
(() => {
  const url = appsmith.store.ccpuUrl;
  const rows = ListeCCPU.selectedRows || [];

  // 1) Pas d'URL → invisible
  if (!url) return false;

  // 2) Pas de sélection → invisible
  if (!rows.length) return false;

  // 3) Tous les codes identiques ?
  const codes = Array.from(new Set(rows.map(r => String(r.code || "").trim())));
  if (codes.length !== 1) return false;

  // 4) Tous les statuts found_unique ou path_ok ?
  const ok = rows.every(r =>
    r.search_status === "found_unique" ||
    r.search_status === "path_ok"
  );
  if (!ok) return false;

  // Sinon → visible
  return true;
})()
}}
