@charset "UTF-8";
/* Toolbar zoom */
.planning-toolbar {
  display: flex;
  gap: 8px;
  margin-bottom: 6px;
}

.planning-toolbar button {
  font-size: 11px;
  padding: 4px 8px;
  border-radius: 4px;
  border: 1px solid #d1d5db;
  background: #f9fafb;
  cursor: pointer;
}

.planning-toolbar button:hover {
  background: #e5e7eb;
}

/* Le Gantt occupe tout l'espace du widget */
#gantt_here {
  width: 100%;
  min-height: 1000px; /* hauteur minimum visible */
}

/* Couleurs des tâches suivant le statut */
.gantt_task_line.status-A_FAIRE {
  background-color: #cdd5df;
  border-color: #b91c1c;
}

.gantt_task_line.status-EN_COURS {
  background-color: #eab308;
  border-color: #a16207;
}

.gantt_task_line.status-COMMENTAIRE_A_TRAITER {
  background-color: #fc9c62;
  border-color: #a16207;
}

.gantt_task_line.status-ATTENTE_CLIENT {
  background-color: #7ac2f0;
  border-color: #a16207;
}

.gantt_task_line.status-TERMINE,
.gantt_task_line.status-VALIDE_CLIENT {
  background-color: #22c55e;
  border-color: #15803d;
}

/* Week-end légèrement grisé */
.gantt_scale_cell.weekend,
.gantt_task_cell.weekend {
  background-color: #f3f4f6;
}

/* ===== Fond rouge léger pour le jour courant ===== */
/* En-tête (échelle) */
.gantt_scale_cell.today-scale {
  background-color: #fee2e2; /* rouge très clair */
}

/* Cellules de la timeline */
.gantt_task_cell.today-cell {
  background-color: #fee2e2;
}

/* ===== Ligne rouge "aujourd'hui" ===== */
.gantt_marker.today-marker {
  z-index: 3; /* au-dessus des barres */
}

/* La vraie ligne rouge verticale */
.gantt_marker.today-marker .gantt_marker_content {
  background-color: #ef4444; /* rouge */
  width: 2px;
  margin-left: -1px; /* pour la centrer sur la date */
}

/* ===== JALON DELAI EN LOSANGE ===== */
/* ===== JALON DELAI EN LOSANGE (corrigé) ===== */
/* On neutralise la barre pour le jalon */
.gantt_task_line.deadline-milestone {
  background: transparent !important;
  border: none !important;
}

/* On masque contenu & progression : on ne garde que le losange */
.gantt_task_line.deadline-milestone .gantt_task_content,
.gantt_task_line.deadline-milestone .gantt_task_progress {
  display: none !important;
}

/* Losange centré sur la ligne (vert. & horiz.) */
.gantt_task_line.deadline-milestone::before {
  content: "";
  position: absolute;
  /* centre horizontalement sur la barre (donc sur le jour du délai) */
  left: 50%;
  margin-left: -5px;
  /* centre verticalement sur la ligne */
  top: 50%;
  margin-top: -5px;
  width: 10px;
  height: 10px;
  transform: rotate(45deg);
  background: #cf3232; /* couleur du jalon */
  border-radius: 2px;
}

/* ===== Jalons pour APPRO & ACHATS ===== */
/* On supprime complètement la barre */
.gantt_task_line.step-milestone {
  background: transparent !important;
  border: none !important;
}

/* On masque tout contenu interne */
.gantt_task_line.step-milestone .gantt_task_content,
.gantt_task_line.step-milestone .gantt_task_progress {
  display: none !important;
}

/* On dessine un losange coloré selon statut */
.gantt_task_line.step-milestone::before {
  content: "";
  position: absolute;
  left: 50%;
  top: 50%;
  margin-left: -5px;
  margin-top: -5px;
  width: 10px;
  height: 10px;
  transform: rotate(45deg);
  border-radius: 2px;
  /* couleur via classe "status-XXX" */
}

/* Couleur via statut */
.gantt_task_line.step-milestone.status-A_FAIRE::before {
  background: #cdd5df;
}

.gantt_task_line.step-milestone.status-EN_COURS::before,
.gantt_task_line.step-milestone.status-ATTENTE_CLIENT::before {
  background: #eab308;
}

.gantt_task_line.step-milestone.status-TERMINE::before,
.gantt_task_line.step-milestone.status-VALIDE_CLIENT::before {
  background: #22c55e;
}

/* ===== Taille des textes ===== */
/* Réduction taille du texte dans la grille (tableau à gauche) */
.gantt_grid,
.gantt_grid * {
  font-size: 10px !important;
}

/* Réduction taille du texte dans la timeline */
.gantt_task,
.gantt_task * {
  font-size: 10px !important;
}

/* Réduction taille du header grille */
.gantt_grid_head_cell {
  font-size: 11px !important;
}

/* Réduction taille du header timeline */
.gantt_scale_cell {
  font-size: 11px !important;
}

/* Barre de regroupement (ligne appareil) */
.gantt_task_line.appareil-group {
  background: #111 !important;
  border-color: #111 !important;
  border-radius: 6px !important;
  height: 16px !important; /* barre plus "fine" */
  margin-top: 6px !important; /* centre visuellement dans la ligne */
}

/* Texte à l'intérieur de la barre project (si affiché) */
.gantt_task_line.appareil-group .gantt_task_content {
  color: #fff !important;
  font-weight: 600 !important;
  font-size: 12px !important;
}

/* Optionnel : enlever le "progress" des projects */
.gantt_task_line.appareil-group .gantt_task_progress {
  display: none !important;
}

.gantt_task_line.appareil-group .gantt_task_content {
  display: none !important;
}

