// -------- Helpers généraux --------
// Ramène une date (YYYY-MM-DD) au dernier jour ouvré si elle tombe le week-end
function normalizeToBusinessEnd(endIso) {
  if (!endIso) return null;
  const d = new Date(endIso + "T00:00:00Z");
  let day = d.getUTCDay(); // 0 = dimanche, 6 = samedi

  if (day === 0) {
    // dimanche -> vendredi
    d.setUTCDate(d.getUTCDate() - 2);
  } else if (day === 6) {
    // samedi -> vendredi
    d.setUTCDate(d.getUTCDate() - 1);
  }
  return d.toISOString().slice(0, 10);
}

// Calcule automatiquement la largeur idéale de la colonne Task name
function computeTaskColumnWidth(tasks) {
  let maxChars = 10; // nombre minimal de caractères

  (tasks || []).forEach(t => {
    const label = t.type === "project" ? t.nom_appareil || t.text || "" : t.text || "";
    if (label.length > maxChars) {
      maxChars = label.length;
    }
  });

  // approx : 7 px par caractère + padding
  return Math.min(maxChars * 7 + 40, 600); // limite max 600px
}

// Charge dhtmlxGantt une seule fois
function ensureGanttLoaded() {
  if (window.gantt) return Promise.resolve();
  if (window.ganttLoading) return window.ganttLoading;
  window.ganttLoading = new Promise((resolve, reject) => {
    const script = document.createElement("script");
    script.src = "https://cdn.dhtmlx.com/gantt/edge/dhtmlxgantt.js";
    script.onload = () => resolve();
    script.onerror = reject;
    document.head.appendChild(script);
  });
  return window.ganttLoading;
}

// Format YYYY-MM-DD -> DD-MM-YYYY
function formatDateFr(isoStr) {
  if (!isoStr) return "";
  const s = String(isoStr).slice(0, 10); // garde que AAAA-MM-JJ
  const parts = s.split("-");
  if (parts.length !== 3) return s;
  const [y, m, d] = parts;
  return `${d}-${m}-${y}`;
}

// -------- Helpers pour jours ouvrés / nombre_jours --------

function parsePositiveInt(val) {
  const n = parseInt(val, 10);
  return Number.isFinite(n) && n > 0 ? n : null;
}

// endIso = "YYYY-MM-DD", nbDays = nombre de jours ouvrés (>=1)
// On recule nbDays-1 jours ouvrés pour avoir la date de début incluse
function subtractBusinessDays(endIso, nbDays) {
  if (!endIso) return null;
  const d = new Date(endIso + "T00:00:00Z");

  // si nbDays vaut 1 -> on ne recule pas (début = fin)
  let remaining = Math.max(nbDays - 1, 0);
  while (remaining > 0) {
    d.setUTCDate(d.getUTCDate() - 1);
    const day = d.getUTCDay(); // 0 = dimanche, 6 = samedi
    if (day !== 0 && day !== 6) {
      remaining--;
    }
  }
  return d.toISOString().slice(0, 10);
}

// Calcule start/end d'une étape à partir de date_objectif (fin) et nombre_jours
// - date_objectif peut tomber le week-end : on la ramène au vendredi
// - nombre_jours = nb de jours ouvrés
// Retourne { startStr, endStr } pour Gantt (endStr exclusive)
function computeChildTaskDates(etape) {
  const code = etape.groupe_code;

  // ✅ Cas spécial FABRICATION : on prend les dates fournies par la query
  if (code === "FABRICATION") {
    const rawStart = etape.date_debut;
    const rawEnd = etape.date_objectif || etape.date_fin;
    if (!rawStart || !rawEnd) return null;
    const startIso = String(rawStart).slice(0, 10);
    const endIso = String(rawEnd).slice(0, 10);

    // On normalise la fin si elle tombe le week-end (par sécurité)
    const endBusiness = normalizeToBusinessEnd(endIso);
    if (!endBusiness) return null;

    // start_date inclusive (tel quel, normalement déjà ouvré si calculé en SQL)
    const startStr = startIso;

    // end_date exclusive = fin ouvrée + 1 jour
    const dEnd = new Date(endBusiness + "T00:00:00Z");
    dEnd.setUTCDate(dEnd.getUTCDate() + 1);
    const endStr = dEnd.toISOString().slice(0, 10);
    return {
      startStr,
      endStr
    };
  }

  // --- comportement actuel pour les autres étapes ---
  const rawEnd = etape.date_objectif || etape.date_fin;
  if (!rawEnd) return null;
  const rawEndIso = String(rawEnd).slice(0, 10);
  const endBusiness = normalizeToBusinessEnd(rawEndIso);
  if (!endBusiness) return null;
  const nbJours = parsePositiveInt(etape.nombre_jours) || 1;
  const startStr = subtractBusinessDays(endBusiness, nbJours);
  const dEnd = new Date(endBusiness + "T00:00:00Z");
  dEnd.setUTCDate(dEnd.getUTCDate() + 1);
  const endStr = dEnd.toISOString().slice(0, 10);
  return {
    startStr,
    endStr
  };
}

// Gestion du zoom (échelles jour / semaine / mois)
function setScale(level) {
  if (!window.gantt) return;
  switch (level) {
    case "day":
      gantt.config.scales = [{
        unit: "month",
        step: 1,
        format: "%F %Y"
      }, {
        unit: "day",
        step: 1,
        format: "%d"
      }];
      break;
    case "month":
      gantt.config.scales = [{
        unit: "year",
        step: 1,
        format: "%Y"
      }, {
        unit: "month",
        step: 1,
        format: "%M"
      }];
      break;
    case "week":
    default:
      gantt.config.scales = [{
        unit: "year",
        step: 1,
        format: "%Y"
      }, {
        unit: "week",
        step: 1,
        format: function (date) {
          const weekStr = gantt.date.date_to_str("%W")(date);
          return "S" + weekStr;
        }
      }, {
        unit: "day",
        step: 1,
        format: "%d"
      }];
      break;
  }
  gantt.render();
}

// ----- Marqueur "aujourd'hui" (ligne rouge) -----
function refreshTodayMarker() {
  if (!window.gantt || !gantt.addMarker) return;
  const today = new Date();

  // Supprimer l'ancien marqueur si besoin
  if (window.todayMarkerId) {
    gantt.deleteMarker(window.todayMarkerId);
  }

  // Ajouter un nouveau marqueur
  window.todayMarkerId = gantt.addMarker({
    start_date: today,
    css: "today-marker",
    text: "",
    title: ""
  });
  gantt.renderMarkers();
}

// ----- Centrer le planning sur la date du jour -----
function centerOnToday(offsetRatio = 0.5) {
  if (!window.gantt) return;
  const today = new Date();

  // Position X (en pixels) de la date aujourd'hui
  const posX = gantt.posFromDate(today);

  // Largeur visible de la timeline
  const viewWidth = gantt.$task.offsetWidth;

  // Offset : 0.35 = aujourd'hui un peu à droite du centre
  const targetScroll = posX - Math.floor(viewWidth * offsetRatio);
  gantt.scrollTo(targetScroll, null);
}

// -------- Mapping des données Appsmith -> Gantt --------

// Transforme QryDashboardSuiviFabrication.data -> { data: tasks, links }
function mapRowsToGantt(rows) {
  const tasks = [];
  const links = [];
  rows.forEach(r => {
    const appareilId = String(r.numero_appareil);
    const appareilLabel = `${r.nom_appareil}`;
    const etapes = Array.isArray(r.etapes) ? r.etapes : [];
    const delaiStr = r.delai ? String(r.delai).slice(0, 10) : null;
    const today = new Date().toISOString().slice(0, 10);

    // --- préparation des étapes avec dates calculées (jours ouvrés) ---
    const preparedEtapes = [];
    etapes.forEach((e, idx) => {
      const dates = computeChildTaskDates(e);
      if (!dates) return;
      preparedEtapes.push({
        e,
        idx,
        startStr: dates.startStr,
        endStr: dates.endStr
      });
    });

    // --- bornes min/max pour la barre projet ---
    let firstStepStart = null;
    let lastStepEnd = null;
    preparedEtapes.forEach(p => {
      if (!firstStepStart || p.startStr < firstStepStart) {
        firstStepStart = p.startStr;
      }
      if (!lastStepEnd || p.endStr > lastStepEnd) {
        lastStepEnd = p.endStr;
      }
    });
    const projectStart = firstStepStart || delaiStr || today;
    const projectEnd = delaiStr || lastStepEnd || projectStart;

    // --- tâche "projet" pour l'appareil ---
    tasks.push({
      id: appareilId,
      text: appareilLabel,
      nom_appareil: r.nom_appareil,
      start_date: projectStart,
      end_date: projectEnd,
      delai: delaiStr,
      type: "project",
      open: true
    });

    // --- étapes enfants ---
    const stepIdByCode = {}; // ex: { NDC: "...", PLAN: "...", ... }

    preparedEtapes.forEach(p => {
      const e = p.e;
      const idx = p.idx;
      const code = e.groupe_code || `ETAPE_${idx}`;
      const libelle = e.groupe_libelle || code;
      const childId = `${appareilId}_${code}_${idx}`;

      // Flags jalons pour APPRO et ACHATS
      const isAppro = code === "BESOINS";
      const isAchats = code === "ACHATS"; // adapte si besoin

      tasks.push({
        id: childId,
        text: libelle,
        start_date: p.startStr,
        end_date: p.endStr,
        parent: appareilId,
        progress: 0,
        status: e.statut_code,
        date_objectif: e.date_objectif ? String(e.date_objectif).slice(0, 10) : null,
        nombre_jours: e.nombre_jours,
        groupe_code: e.groupe_code,
        groupe_appareil_id: e.groupe_appareil_id,
        // tâche affichée comme jalon, PAS comme une barre pour APPRO / ACHATS
        isStepMilestone: isAppro || isAchats
      });

      // On mémorise l'id par code (si doublons, on garde le dernier)
      stepIdByCode[code] = childId;
    });

    // --- Dépendances (liens) selon le nouvel enchaînement ---
    function addLink(srcCode, tgtCode) {
      const source = stepIdByCode[srcCode];
      const target = stepIdByCode[tgtCode];
      if (!source || !target) return; // étape manquante => pas de lien
      links.push({
        id: `${source}->${target}`,
        source,
        target,
        type: 0 // Finish-to-Start
      });
    }

    // NDC -> PLAN
    addLink("NOTE_CALCUL", "PLAN");

    // PLAN -> (CDS + PREPA) en parallèle
    addLink("PLAN", "CAHIER_SOUDAGE");
    addLink("PLAN", "PREPARATION");

    // Pas de sortie après CDS => rien à faire

    // PREPA -> (APPRO + ACHATS) en parallèle
    addLink("PREPARATION", "BESOINS");
    addLink("PREPARATION", "ACHATS");

    // (APPRO + ACHATS) -> FAB
    addLink("BESOINS", "FABRICATION");
    addLink("ACHATS", "FABRICATION");

    // --- JALON DELAI : toujours en dernier enfant ---
    if (delaiStr) {
      tasks.push({
        id: appareilId + "_deadline",
        text: "Délai",
        start_date: delaiStr,
        // pour le positionnement sur le planning
        parent: appareilId,
        isDeadline: true,
        date_objectif: delaiStr // pour l'affichage dans "Date obj."
      });
    }
  });
  return {
    data: tasks,
    links
  };
}

// -------- Initialisation + rendu Gantt --------

async function renderGantt() {
  const rows = appsmith.model && appsmith.model.rows || [];
  if (!Array.isArray(rows) || !rows.length) return;
  await ensureGanttLoaded();

  // on mappe D'ABORD les données -> tasks + links
  const ganttData = mapRowsToGantt(rows);

  // on calcule la largeur idéale de la colonne "Désignation"
  const idealWidth = computeTaskColumnWidth(ganttData.data);

  // Init une seule fois
  if (!window.ganttInitialized) {
    // activer le plugin "marker" pour la ligne rouge aujourd'hui
    if (gantt.plugins) {
      gantt.plugins({
        marker: true
      });
    }
    gantt.config.show_markers = true;

    // lecture seule
    gantt.config.readonly = true;
    gantt.config.date_format = "%Y-%m-%d";
    gantt.config.min_column_width = 30; // taille des cases "Jour"
    gantt.config.grid_resize = true;

    // Colonnes du tableau à gauche
    gantt.config.columns = [{
      name: "text",
      label: "Désignation",
      tree: true,
      width: idealWidth,
      min_width: 120,
      template: function (task) {
        // Pour les appareils : uniquement le nom_appareil
        if (task.type === "project") {
          return task.nom_appareil || task.text;
        }
        // Pour les étapes : libellé normal
        return task.text;
      }
    }, {
      // Colonne date objectif :
      // appareil  -> délai de l'appareil
      // étape     -> date objectif de l'étape
      name: "start_custom",
      label: "Date obj.",
      align: "center",
      width: 100,
      template: function (task) {
        if (task.type === "project") {
          const d = task.delai || task.start_date;
          return formatDateFr(d);
        }
        const d = task.date_objectif || task.start_date;
        return formatDateFr(d);
      }
    }];

    // Week-end en gris + jour courant en rouge clair
    const todayStr = new Date().toISOString().slice(0, 10); // "YYYY-MM-DD"
    const fmtIso = gantt.date.date_to_str("%Y-%m-%d");
    gantt.templates.scale_cell_class = function (date) {
      const day = date.getDay();
      const dStr = fmtIso(date);
      const classes = [];
      if (day === 0 || day === 6) classes.push("weekend");
      if (dStr === todayStr) classes.push("today-scale");
      return classes.join(" ");
    };
    gantt.templates.timeline_cell_class = function (task, date) {
      const day = date.getDay();
      const dStr = fmtIso(date);
      const classes = [];
      if (day === 0 || day === 6) classes.push("weekend");
      if (dStr === todayStr) classes.push("today-cell");
      return classes.join(" ");
    };

    // Classe CSS selon le statut + jalons
    gantt.templates.task_class = function (start, end, task) {
      const classes = [];
      if (task.type === "project") classes.push("appareil-group");

      // jalon délai (ligne "Délai")
      if (task.isDeadline) classes.push("deadline-milestone");

      // jalon pour APPRO / ACHATS
      if (task.isStepMilestone) classes.push("step-milestone");

      // statut (couleur)
      if (task.status) classes.push("status-" + task.status);
      return classes.join(" ");
    };

    // Init gantt
    gantt.init("gantt_here");
    window.ganttInitialized = true;

    // Zoom par défaut : vue semaine
    setScale("week");

    // Gestion des boutons de zoom dans la toolbar HTML
    const toolbar = document.querySelector(".planning-toolbar");
    if (toolbar) {
      toolbar.addEventListener("click", function (e) {
        const btn = e.target.closest("button[data-zoom]");
        if (!btn) return;
        const level = btn.getAttribute("data-zoom");
        setScale(level);
      });
    }
  } else {
    // si déjà initialisé, on met simplement à jour la largeur de la 1ère colonne
    if (gantt.config.columns && gantt.config.columns.length > 0) {
      gantt.config.columns[0].width = idealWidth;
    }
  }

  // on applique les données
  gantt.clearAll();
  gantt.parse(ganttData);

  // Marqueur "aujourd'hui" + centrage
  refreshTodayMarker();
  setTimeout(centerOnToday, 50); // petit délai pour que le DOM ait sa largeur définitive
}

// Initialisation et rafraîchissements
appsmith.onReady(renderGantt);
appsmith.onModelChange(renderGantt);

