isDisabled:
{{
(() => {
  const cand = ListeCandidatsCCPU.selectedRow;

  // Désactivé si aucune sélection
  if (!cand) return true;

  // Désactivé si pas de chemin (path vide ou inexistant)
  if (!cand.path || String(cand.path).trim() === "") return true;

  return false; // bouton activé
})()
}}

onClick:
{{
(() => {
  const cand = ListeCandidatsCCPU.selectedRow;

  if (!cand || !cand.path) {
    showAlert("Sélectionne d'abord un fichier candidat.", "warning");
    return;
  }

  const rows = appsmith.store.ccpuSelectedRows || [];
  const baseCode = rows.length ? String(rows[0].code || "").trim() : "";

  let url =
    "https://n8n.kynthos.fr/webhook/appsmith-ccpu-file-candidat" +
    `?srcPath=${encodeURIComponent(cand.path)}`;

  if (baseCode) {
    url += `&code=${encodeURIComponent(baseCode)}`;
  }

  storeValue("ccpuCandidateUrl", url);
})()
}}
