isDisabled:
{{
(() => {
  const cand = ListeCandidatsCCPU.selectedRow;
  const url  = appsmith.store.ccpuCandidateUrl;

  // 1) Pas de candidat / pas de path / pas d'URL -> bouton désactivé
  if (!cand || !cand.path || !url) return true;

  // 2) Code attendu = code de la première ligne pré-sélectionnée
  const rows = appsmith.store.ccpuSelectedRows || [];
  if (!rows.length) return true;

  const expectedCode = String(rows[0].code || "").trim();
  if (!expectedCode) return true;

  // 3) Récupérer le paramètre ?code=... dans l'URL du viewer 2
  const match = url.match(/[?&]code=([^&]+)/);
  if (!match) return true;         // pas de code dans l'URL -> désactivé

  const urlCode = decodeURIComponent(match[1] || "");

  // 4) Désactivé si le code de l'URL est différent du code attendu
  return urlCode !== expectedCode;
})()
}}

onClick:
{{
  (async () => {
    const cand = ListeCandidatsCCPU.selectedRow;

    if (!cand) {
      showAlert("Sélectionne d'abord un fichier candidat.", "warning");
      return;
    }

    const rows = appsmith.store.ccpuSelectedRows || [];
    if (!rows.length) {
      showAlert("Aucune ligne à alimenter (sélection perdue, rouvre la fenêtre).", "error");
      return;
    }

    const numAff = CaracteristiquesAppareils.data?.[0]?.numero_affaire;
    const nomCli = DonneesGeneralesAffaire.data?.[0]?.nom_client;
    const nomApp = CaracteristiquesAppareils.data?.[0]?.nom_appareil;

    if (!numAff || !nomCli || !nomApp) {
      showAlert("Informations affaire/appareil incomplètes.", "error");
      return;
    }

    // 0) Tous les codes doivent être identiques (normalement déjà le cas)
    const codes = Array.from(
      new Set(rows.map(r => String(r.code || "").trim()))
    );
    if (codes.length !== 1) {
      showAlert("Toutes les lignes doivent avoir le même CCPU.", "error");
      return;
    }
    const code = codes[0];

    // 1) Mise à jour BDD (src_path + search_status = 'path_ok' dans ta requête UpdateCCPUPath)
    const dbTasks = rows.map(r => {
      const srsm =
        r.srsm == null || String(r.srsm).trim() === ""
          ? "1"
          : String(r.srsm).trim();

      return UpdateCCPUPath.run({
        numero_affaire: numAff,
        nom_appareil: nomApp,
        rep: r.rep,
        srsm: srsm,
        code: r.code,
        src_path: cand.path,
      });
    });

    const dbResults = await Promise.allSettled(dbTasks);
    const okDb = dbResults.filter(r => r.status === "fulfilled").length;
    const koDb = dbResults.length - okDb;

    // 2) Copie physique dans "Certificat de contrôle des matériaux" via n8n
    //    (workflow /appsmith-ccpu-copy déjà configuré avec l'API CCPUCopyApi)
    await CCPUCopyApi.run({
      srcPath: cand.path,        // chemin NAS du candidat
      code: code,                // code unique
      rows: rows.map(r => ({     // rep/srsm/code pour chaque ligne à copier
        rep: r.rep,
        srsm: r.srsm,
        code: r.code,
      })),
    });

    showAlert(
      `Mise à jour effectuée — OK: ${okDb}${koDb ? " / Échecs MAJ: " + koDb : ""} | CCPU copié dans 'Certificat de contrôle des matériaux'.`,
      koDb ? "warning" : "success"
    );

    // 3) Rafraîchir les statuts / liste
    await QryCCPUFull.run();

    closeModal(ModalChoixCCPU.name);
  })()
}}
