onSelectionChange:
{{
  (async () => {
    const lang = (RadioLangue.selectedOptionValue || 'fr').toLowerCase();

    // 1) mettre à jour l'état local pour re-render immédiat
    await storeValue('langue_appareil', lang);

    // 2) persister en base
    await UpdateLangueAppareil.run();

    // 3) rafraîchir la requête des données générales (optionnel mais cohérent)
    await DonneesGeneralesAffaire.run();

    // 4) pas obligatoire pour la langue, mais ok si tu veux recharger la liste
    // await GetSommaireAppareil.run();
  })().catch(e => showAlert('Erreur langue: ' + (e?.message || e), 'error'))
}}
