{{
  (() => {
    // üîÅ Remplace par les noms EXACTS de tes DatePickers
    const start = DateDebutFab.selectedDate;
    const end   = DateFinFab.selectedDate;

    if (!start || !end) return "";

    let d1 = new Date(start);
    let d2 = new Date(end);

    // S√©curise l'ordre
    if (d1 > d2) [d1, d2] = [d2, d1];

    // Normalisation √† minuit UTC
    d1 = new Date(Date.UTC(d1.getFullYear(), d1.getMonth(), d1.getDate()));
    d2 = new Date(Date.UTC(d2.getFullYear(), d2.getMonth(), d2.getDate()));

    let workingDays = 0;

    // Jours ouvr√©s INCLUSIFS (d√©but et fin inclus)
    for (let d = new Date(d1); d <= d2; d.setUTCDate(d.getUTCDate() + 1)) {
      const day = d.getUTCDay(); // 0=dim, 6=sam
      if (day !== 0 && day !== 6) workingDays++;
    }

    const HOURS_PER_DAY = 7.5;

    return workingDays * HOURS_PER_DAY;
  })()
}}
