isVisible:
{{
(() => {
  const url = appsmith.store.ccpuUrl;
  const rows = ListeCCPU.selectedRows || [];

  // 1) Pas d'URL → invisible
  if (!url) return false;

  // 2) Pas de sélection → invisible
  if (!rows.length) return false;

  // 3) Tous les codes identiques ?
  const codes = Array.from(
    new Set(rows.map(r => String(r.code || "").trim()))
  );
  if (codes.length !== 1) return false;

  const selectedCode = codes[0];

  // 4) Vérifier que l'URL contient bien ce code
  //    → on cherche dans l'URL le code exact (ex : "...?code=5G230")
  if (!url.includes(selectedCode)) return false;

  // 5) Statuts autorisés
  const validStatuses = ["found_unique", "path_ok"];
  const allValid = rows.every(r => validStatuses.includes(r.search_status));
  if (!allValid) return false;

  // 6) Sinon → visible
  return true;
})()
}}
