{{
(() => {
  const rows = QrySuiviFab_Statuts.data || [];

  return `
    <div style="
      display:flex;
      flex-wrap:nowrap;      /* empêche le retour à la ligne */
      white-space:nowrap;    /* garantit une seule ligne */
      align-items:center;
      column-gap:5px;       /* espace entre chaque bloc */
    ">
      ${
        rows.map(r => `
          <span style="display:inline-flex; align-items:center; font-size:0.7rem;">
            <span style="
              width:10px;
              height:10px;
              background:${r.couleur};
              border:1px solid #ccc;
              border-radius:2px;
              margin-right:4px;
            "></span>
            <span>${r.libelle}</span>
          </span>
        `).join("")
      }
    </div>
  `;
})()
}}
